CREATE PROCEDURE [dbo].[proc_RDL_GetTranslationSet](@UserID UNIQUEIDENTIFIER, @TypeID INTEGER)
AS
          SET NOCOUNT ON;

		 -- declare @UserID UNIQUEIDENTIFIER, @TypeID INTEGER
			--select @UserID = N'8c7689b0-663b-49e3-885a-58611a88da4c', @TypeID = 113

          DECLARE   @Culture NCHAR(5),
					@UserName NVARCHAR(MAX),
					@ExecutionDateTime DATETIME,
					@ExecutionTime NVARCHAR(MAX),
					@Currency NVARCHAR(MAX),
					@Speedunit NVARCHAR(MAX),
					@SpeedunitTranslate NVARCHAR(MAX)

          DECLARE   @Set TABLE([Key] NVARCHAR(MAX), Value NVARCHAR(MAX), ValueBits VARBINARY(MAX));

		  -- Get the user culture preference
          SELECT    TOP 1 @Currency = Value
          FROM      UserPreference
          WHERE     NameID = 381
          AND       userID = @UserID;

		  SET @Currency = ISNULL(@Currency, 'Â£')

		  -- Get the user speed unit string preference
          SELECT    TOP 1 @Speedunit = Value
          FROM      UserPreference
          WHERE     NameID = 203
          AND       userID = @UserID;
		  
		  SET @Speedunit = ISNULL(@Speedunit, 'KM')

		  SELECT TOP 1 @SpeedunitTranslate = T.Value
		  FROM      ReportTranslationSet S
                    INNER JOIN ReportTranslation T ON T.SetID = S.SetID
          WHERE     T.[Key] =@Speedunit AND S.Culture = @Culture;
		  
		  SET @SpeedunitTranslate = ISNULL(@SpeedunitTranslate, @Speedunit)

		  -- Get the user currency preference
          SELECT    TOP 1 @Culture = Value
          FROM      UserPreference
          WHERE     NameID = 310
          AND       userID = @UserID;

		  -- Get the formatted user name (or name + surname)
		  DECLARE	@firstname NVARCHAR(MAX),
					@surname NVARCHAR(MAX)
		  SELECT	TOP 1 @UserName = [Name], @firstname = FirstName, @surname = Surname
		  FROM		dbo.[User]
		  WHERE		UserID = @UserID;
		  SET @UserName = CASE WHEN @surname IS NULL AND @firstname IS NULL 
							   THEN @UserName 
							   ELSE CASE WHEN @firstname IS NULL
									THEN @surname
									ELSE @firstname + ' ' + ISNULL(@surname, '')
									END
							   END
		  
		  -- Get statistical data
		  SET		@ExecutionDateTime = dbo.TZ_GetTime(GETUTCDATE(), DEFAULT, @UserID)
		  SET		@ExecutionTime = CONVERT(VARCHAR(10), @ExecutionDateTime, 103) + ' '  + convert(VARCHAR(5), @ExecutionDateTime, 108)
		  
		  --Get Translations
          INSERT    @Set
          SELECT    T.[Key], T.Value, CAST(0 AS VARBINARY(MAX))
          FROM      ReportTranslationSet S
                    INNER JOIN ReportTranslation T ON T.SetID = S.SetID
          WHERE     S.WidgetTypeID = @TypeID
          AND       S.Culture = @Culture;

		  -- If given culture has no translations available, get defaults
          IF (SELECT COUNT(1) FROM @Set) < 1
                    SELECT    T.[Key], T.Value, CAST(0 AS VARBINARY(MAX)) AS ValueBits
                    FROM      ReportTranslationSet S
                              INNER JOIN ReportTranslation T ON T.SetID = S.SetID
                    WHERE     S.WidgetTypeID = @TypeID
                    AND       S.Culture IS NULL
                    UNION SELECT 'UserName' AS [Key], @UserName AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
                    UNION SELECT 'ExecutionTime' AS [Key], @ExecutionTime AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
					UNION SELECT 'ReportLogoBits' AS [Key], 'RTL' AS [Value],  AS ValueBits
        --            UNION SELECT 'ReportLogoBits' AS [Key], ImageName AS [Value], ImageBits AS ValueBits
						  --FROM Test_Database.dbo.[Image]
						  --WHERE ImageID = 1
                    UNION SELECT [Key], [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits FROM dbo.ReportTranslationGlobal
					UNION SELECT 'Currency' AS [Key], @Currency AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
					UNION SELECT 'DistanceUnit' AS [Key], @SpeedunitTranslate AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
          --Culure has translations, get them
		  ELSE
                    SELECT    *
                    FROM      @Set
                    UNION SELECT 'UserName' AS [Key], @UserName AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits 
                    UNION SELECT 'ExecutionTime' AS [Key], @ExecutionTime AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
					UNION SELECT 'ReportLogoBits' AS [Key], 'RTL' AS [Value],  AS ValueBits
					--UNION SELECT 'ReportLogoBits' AS [Key], ImageName AS [Value], ImageBits AS ValueBits
						  --FROM Test_Database.dbo.[Image]
						  --WHERE ImageID = 1
                    UNION SELECT [Key], [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits FROM dbo.ReportTranslationGlobal
					UNION SELECT 'Currency' AS [Key], @Currency AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits
					UNION SELECT 'DistanceUnit' AS [Key], @SpeedunitTranslate AS [Value], CAST(0 AS VARBINARY(MAX)) AS ValueBits;
			
		/*
		Result data set consists of three things:
		1. Translations for @Set
		2. Various lookup results like User name, exec. time, etc.
		3. Output from ReportTranslationGlobal 
		*/

GO
