DECLARE @lvids NVARCHAR(MAX),
		@lgids NVARCHAR(MAX),
		@lsdate DATETIME,
		@ledate DATETIME,
		@luid UNIQUEIDENTIFIER,
		@gid UNIQUEIDENTIFIER
		
SET @lvids = @vids
SET @lgids = @gids
SET @lsdate = @sdate
SET @ledate = @edate
SET @luid = @uid

DECLARE @tempmult FLOAT,
		@liquidmult FLOAT,
		@sensorid INT,
		@analogscaling FLOAT,
		@scount INT

-- Hardcoded values for this report only
SET @sensorid = 2
SET @analogscaling = 0.00390625

DECLARE @histogram TABLE (
		hid INT IDENTITY (1,1),
		TempLow FLOAT,
		TempHigh FLOAT)
      
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-999.0, -32.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-32.0, -30.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-30.0, -28.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-28.0, -26.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-26.0, -24.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-24.0, -22.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-22.0, -20.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-20.0, -18.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-18.0, -16.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-16.0, -14.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-14.0, -12.0)
INSERT INTO @histogram (TempLow, TempHigh) VALUES (-12.0, 999.0)

-- Convert dates to UTC
SET @lsdate = [dbo].TZ_ToUTC(@lsdate,default,@luid)
SET @ledate = [dbo].TZ_ToUTC(@ledate,default,@luid)

SET @tempmult = Cast(ISNULL(.[dbo].[UserPref](@luid, 214),1) as float)
SET @liquidmult = Cast(ISNULL([dbo].[UserPref](@luid, 200),1) as float)

SELECT	g.GroupId,
		g.GroupName,
		v.VehicleId,
		v.Registration,
		h.TempLow,
		h.TempHigh,
		cuberesult.NumPlots
		
FROM
(	SELECT  
			CASE WHEN (GROUPING(g.GroupId) = 1) THEN NULL
				ELSE ISNULL(g.GroupId, NULL)
			END AS GroupId,

			CASE WHEN (GROUPING(v.VehicleId) = 1) THEN NULL
				ELSE ISNULL(v.VehicleId, NULL)
			END AS VehicleId,

			h.hid,
			COUNT(e.EventId) AS NumPlots

	FROM    dbo.[Event] e WITH (NOLOCK)
	INNER JOIN dbo.Vehicle v ON v.VehicleIntID = e.VehicleIntID
	INNER JOIN dbo.GroupDetail gd ON v.VehicleId = gd.EntityDataId
	INNER JOIN dbo.[Group] g ON gd.GroupId = g.GroupId
	INNER JOIN @histogram h ON dbo.ScaleConvertAnalogValue(e.AnalogData1, @AnalogScaling, @tempmult, @liquidmult) > h.TempLow
							AND dbo.ScaleConvertAnalogValue(e.AnalogData1, @AnalogScaling, @tempmult, @liquidmult) <= h.TempHigh
	WHERE   e.EventDateTime BETWEEN @lsdate AND @ledate
			AND e.CreationCodeId IS NOT NULL
			AND e.CreationCodeId NOT IN (24, 91)
			AND (e.Lat != 0 AND e.Long != 0)
			AND v.VehicleId IN (SELECT VALUE FROM dbo.Split(@lvids, ','))
			AND g.GroupId IN (SELECT VALUE FROM dbo.Split(@lgids, ','))
	GROUP BY g.GroupId,v.VehicleId, h.hid WITH CUBE
) cuberesult
LEFT JOIN dbo.Vehicle v ON cuberesult.VehicleId = v.VehicleId
LEFT JOIN [Group] g ON cuberesult.GroupId = g.GroupId
INNER JOIN @histogram h ON cuberesult.hid = h.hid
WHERE cuberesult.GroupId IS NULL OR cuberesult.VehicleId IS NULL
ORDER BY g.GroupName, v.Registration, h.TempLow	
